import timeimport boardimport neopixelimport digitalio # Update this to match the number of NeoPixel LEDs connected to your board.num_pixels = 8 pixels = neopixel.NeoPixel(board.GP0, num_pixels)pixels.brightness = 0.08 while True:        pixels.fill((250, 0, 0))        time.sleep(0.8)        pixels.fill((0, 250, 0))        time.sleep(0.8)         pixels.fill((0, 0, 250))        time.sleep(0.8)                                       